function cikgu_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('cikgu');
	if(!$arrPerm[1]){
		return false;
	}

	$data['nama'] = makeSafe($_REQUEST['nama']);
		if($data['nama'] == empty_lookup_value){ $data['nama'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['phone'] = makeSafe($_REQUEST['phone']);
		if($data['phone'] == empty_lookup_value){ $data['phone'] = ''; }
	$data['alamat'] = makeSafe($_REQUEST['alamat']);
		if($data['alamat'] == empty_lookup_value){ $data['alamat'] = ''; }
	$data['ditambahOleh'] = makeSafe($_REQUEST['ditambahOleh']);
		if($data['ditambahOleh'] == empty_lookup_value){ $data['ditambahOleh'] = ''; }
	$data['adminID'] = makeSafe($_REQUEST['adminID']);
		if($data['adminID'] == empty_lookup_value){ $data['adminID'] = ''; }

	// hook: cikgu_before_insert
	if(function_exists('cikgu_before_insert')){
		$args=array();
		if(!cikgu_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `cikgu` set       `nama`=' . (($data['nama'] !== '' && $data['nama'] !== NULL) ? "'{$data['nama']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `phone`=' . (($data['phone'] !== '' && $data['phone'] !== NULL) ? "'{$data['phone']}'" : 'NULL') . ', `alamat`=' . (($data['alamat'] !== '' && $data['alamat'] !== NULL) ? "'{$data['alamat']}'" : 'NULL') . ', `ditambahOleh`=' . (($data['ditambahOleh'] !== '' && $data['ditambahOleh'] !== NULL) ? "'{$data['ditambahOleh']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"cikgu_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// automatic adminID
	if($_REQUEST['filterer_adminID']){
		sql("update `cikgu` set `adminID`='" . makeSafe($_REQUEST['filterer_adminID']) . "' where `cikguID`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: cikgu_after_insert
	if(function_exists('cikgu_after_insert')){
		$res = sql("select * from `cikgu` where `cikguID`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!cikgu_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('cikgu', $recID, getLoggedMemberID());

	return $recID;
}

function cikgu_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('cikgu');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='cikgu' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='cikgu' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: cikgu_before_delete
	if(function_exists('cikgu_before_delete')){
		$args=array();
		if(!cikgu_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `cikgu` where `cikguID`='$selected_id'", $eo);

	// hook: cikgu_after_delete
	if(function_exists('cikgu_after_delete')){
		$args=array();
		cikgu_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='cikgu' and pkValue='$selected_id'", $eo);
}

function cikgu_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('cikgu');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='cikgu' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='cikgu' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['nama'] = makeSafe($_REQUEST['nama']);
		if($data['nama'] == empty_lookup_value){ $data['nama'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['phone'] = makeSafe($_REQUEST['phone']);
		if($data['phone'] == empty_lookup_value){ $data['phone'] = ''; }
	$data['alamat'] = makeSafe($_REQUEST['alamat']);
		if($data['alamat'] == empty_lookup_value){ $data['alamat'] = ''; }
	$data['ditambahOleh'] = makeSafe($_REQUEST['ditambahOleh']);
		if($data['ditambahOleh'] == empty_lookup_value){ $data['ditambahOleh'] = ''; }
	$data['adminID'] = makeSafe($_REQUEST['adminID']);
		if($data['adminID'] == empty_lookup_value){ $data['adminID'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: cikgu_before_update
	if(function_exists('cikgu_before_update')){
		$args=array();
		if(!cikgu_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `cikgu` set       `nama`=' . (($data['nama'] !== '' && $data['nama'] !== NULL) ? "'{$data['nama']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `phone`=' . (($data['phone'] !== '' && $data['phone'] !== NULL) ? "'{$data['phone']}'" : 'NULL') . ', `alamat`=' . (($data['alamat'] !== '' && $data['alamat'] !== NULL) ? "'{$data['alamat']}'" : 'NULL') . ', `ditambahOleh`=' . (($data['ditambahOleh'] !== '' && $data['ditambahOleh'] !== NULL) ? "'{$data['ditambahOleh']}'" : 'NULL') . " where `cikguID`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="cikgu_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: cikgu_after_update
	if(function_exists('cikgu_after_update')){
		$res = sql("SELECT * FROM `cikgu` WHERE `cikguID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['cikguID'];
		$args = array();
		if(!cikgu_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='cikgu' and pkValue='".makeSafe($selected_id)."'", $eo);

}

