			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#nama').replaceWith('<div class=\"form-control-static\" id=\"nama\">' + (jQuery('#nama').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email').replaceWith('<div class=\"form-control-static\" id=\"email\">' + (jQuery('#email').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email, #email-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#phone').replaceWith('<div class=\"form-control-static\" id=\"phone\">' + (jQuery('#phone').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#alamat').replaceWith('<div class=\"form-control-static\" id=\"alamat\">' + (jQuery('#alamat').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ditambahOleh').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#ditambahOleh_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(ditambahOleh)%%>', $combo_ditambahOleh->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(ditambahOleh)%%>', $combo_ditambahOleh->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(ditambahOleh)%%>', urlencode($combo_ditambahOleh->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(adminID)%%>', $combo_adminID->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(adminID)%%>', $combo_adminID->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(adminID)%%>', urlencode($combo_adminID->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'ditambahOleh' => array('admin', 'DitambahOleh'), 'adminID' => array('admin', 'AdminID'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(cikguID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nama)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(email)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(phone)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(alamat)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ditambahOleh)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(adminID)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(cikguID)%%>', safe_html($urow['cikguID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(cikguID)%%>', html_attr($row['cikguID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(cikguID)%%>', urlencode($urow['cikguID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nama)%%>', safe_html($urow['nama']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nama)%%>', html_attr($row['nama']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama)%%>', urlencode($urow['nama']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(email)%%>', safe_html($urow['email']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(email)%%>', html_attr($row['email']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email)%%>', urlencode($urow['email']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(phone)%%>', safe_html($urow['phone']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(phone)%%>', html_attr($row['phone']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(phone)%%>', urlencode($urow['phone']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(alamat)%%>', safe_html($urow['alamat']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(alamat)%%>', html_attr($row['alamat']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(alamat)%%>', urlencode($urow['alamat']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ditambahOleh)%%>', safe_html($urow['ditambahOleh']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ditambahOleh)%%>', html_attr($row['ditambahOleh']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ditambahOleh)%%>', urlencode($urow['ditambahOleh']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(adminID)%%>', safe_html($urow['adminID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(adminID)%%>', html_attr($row['adminID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(adminID)%%>', urlencode($urow['adminID']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(cikguID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(cikguID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nama)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(email)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(phone)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(phone)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(alamat)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(alamat)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ditambahOleh)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ditambahOleh)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(adminID)%%>', ( $_REQUEST['FilterField'][1]=='7' && $_REQUEST['FilterOperator'][1]=='<=>' ? $combo_adminID->SelectedData : ''), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(adminID)%%>', urlencode( $_REQUEST['FilterField'][1]=='7' && $_REQUEST['FilterOperator'][1]=='<=>' ? $combo_adminID->SelectedData : ''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('emailEdit')){ document.getElementById('emailEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('emailEditLink')){ document.getElementById('emailEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('cikgu');
	if($selected_id){
		$jdata = get_joined_record('cikgu', $selected_id);
		if($jdata === false) $jdata = get_defaults('cikgu');
		$rdata = $row;
	}
	$templateCode .= loadView('cikgu-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: cikgu_dv
	if(function_exists('cikgu_dv')){
		$args=array();
		cikgu_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>